import React from 'react';
import './Counter.css';

let count = 0

const Counter = () => {
  const onPlus = () => {
    count++;
  }
  const onMinus = () => {
    count--;
  }

  return (
    <div className='counter'>
      <button onClick = {onPlus}>-</button>
      <div>0</div>
      <button onClick = {onMinus}>+</button>
    </div>
  )
}

export default Counter;
